const { wait } = require('./utils')

/**
 * Data Processor
 */
class DataProcessor {
  /**
   * Constructor
   */
  constructor() {}

  /**
   * Process data
   * @protected
   * @abstract
   * @param {Object} data - Data
   * @return {Object} Result
   */
  async process(data) {}

  /**
   * Run
   * @param {Object} data - Data
   * @return {Object} Result
   */
  async run(data) {
    for (let i = 0; i < 2; ++i) {
      const { success } = await this.process(data)
      if (success) return { success: true }
      await wait(1000)
    }
    return { success: false, error: '' }
  }
}

module.exports = DataProcessor

const xxhash = require('xxhashjs')

/**
 * URL entity
 */
class UrlEntity {
  /**
   * Constructor
   * @param {string} url - URL
   * @param {Object} scraper - Scraper
   * @param {Object} dataProcessor - Data processor
   */
  constructor(url, scraper, dataProcessor) {
    /** @type {string} */
    this.url = url
    /** @type {Object} */
    this.scraper = scraper
    /** @type {Object} */
    this.dataProcessor = dataProcessor
    /** @type {number} */
    this.attempts = 0
    /** @type {string} */
    this.fingerprint = this.getFingerprint()
  }

  /**
   * Get fingerprint
   * @private
   * @return {string} Fingerprint
   */
  getFingerprint() {
    return xxhash.h64(this.url, 0).toString()
  }
}

/**
 * Data entity
 */
class DataEntity {
  /**
   * Constructor
   * @param {Object} data - Data
   * @param {Object} dataProcessor - Data processor
   */
  constructor(data, dataProcessor) {
    /** @type {Object} */
    this.data = data
    /** @type {Object} */
    this.dataProcessor = dataProcessor
    /** @type {number} */
    this.attempts = 0
  }
}

/**
 * Proxy entity
 */
class ProxyEntity {
  /**
   * Constructor
   * @param {string} host - Host
   * @param {number} port - Port number
   * @param {?string} [username] - Username
   * @param {?string} [password] - Password
   */
  constructor(host, port, username, password) {
    /** @type {string} */
    this.host = host
    /** @type {number} */
    this.port = port
    /** @type {Object} */
    this.auth = { username, password }
  }
}

module.exports = { UrlEntity, DataEntity, ProxyEntity }

